import 'package:firebase_auth/firebase_auth.dart';import 'package:fish_redux/fish_redux.dart';enum GlobalAction{changeThemeColor, changeLocale, setUser}class GlobalActionCreator{  static Action onChangeThemeColor(){    return const Action(GlobalAction.changeThemeColor);  }  static Action onChangeLocal(){    return const Action(GlobalAction.changeLocale);  }  static Action setUser(FirebaseUser user){    return Action(GlobalAction.setUser);  }}