import 'dart:ui';import 'package:firebase_auth/firebase_auth.dart';import 'package:fish_redux/fish_redux.dart';abstract class GlobalBaseState {  Color get themeColor;  set themeColor(Color color);  Locale get locale;  set local(Locale locale);}class GlobalState implements GlobalBaseState, Cloneable<GlobalState> {  @override  Color themeColor;  @override  Locale locale;  @override  GlobalState clone() {    return GlobalState()      ..themeColor = themeColor      ..locale = locale;  }  @override  void set local(Locale locale) {  }}