import 'package:firebase_auth/firebase_auth.dart';import 'package:fish_redux/fish_redux.dart';import 'package:flutter_fly_plout/globalstate/action.dart';import 'package:flutter_fly_plout/globalstate/store.dart';import 'action.dart';import 'state.dart';Effect<MainState> buildEffect() {  return combineEffects(<Object, Effect<MainState>>{    Lifecycle.initState: _onInit,    MainAction.action: _onAction,  });}void _onInit(Action action, Context<MainState> ctx) async{  var _user = await FirebaseAuth.instance.currentUser();  if(_user != null){    GlobalStore.store.dispatch(GlobalActionCreator.setUser(_user));  }}void _onAction(Action action, Context<MainState> ctx) {}