import 'package:firebase_auth/firebase_auth.dart';import 'package:fish_redux/fish_redux.dart';import 'package:flutter/material.dart';import 'package:flutter_fly_plout/globalstate/state.dart';import 'package:flutter_fly_plout/pages/discover/page.dart';import 'package:flutter_fly_plout/pages/home/page.dart';import 'package:flutter_fly_plout/pages/member/page.dart';import 'package:flutter_fly_plout/pages/mine/page.dart';class MainState implements GlobalBaseState, Cloneable<MainState> {  int selectedIndex;  List<Widget> pages;  @override  MainState clone() {    return MainState();  }  @override  Color themeColor;  @override  FirebaseUser user;  @override  void set local(Locale locale) {  }  @override  Locale get locale => null;}MainState initState(Map<String, dynamic> args) {  List<Widget> pageList = [    HomePage().buildPage(null),    DiscoverPagePage().buildPage(null),    MemberPagePage().buildPage(null),    MinePage().buildPage(null)  ];  return MainState()..pages = pageList;}