import 'package:firebase_auth/firebase_auth.dart';import 'package:fish_redux/fish_redux.dart';import 'package:flutter/material.dart';class MainState implements Cloneable<MainState> {  int selectedIndex = 0;  List<Widget> pages;  @override  MainState clone() {    return MainState()      ..pages = pages      ..selectedIndex = selectedIndex;  }}MainState initState(Map<String, dynamic> args) {  return MainState()..pages = args['pages'];}