import 'package:fish_redux/fish_redux.dart';import 'package:flutter/material.dart';import 'package:flutter_fly_plout/config/index.dart';import 'package:flutter_fly_plout/widgets/keepalive_widget.dart';import 'action.dart';import 'state.dart';Widget buildView(MainState state, Dispatch dispatch, ViewService viewService) {  return Builder(    builder: (context) {      final pageController = PageController();      final _lightTheme = ThemeData.light().copyWith(        backgroundColor: Colors.white,        tabBarTheme: TabBarTheme(          labelColor: Color(0XFF505050),          unselectedLabelColor: Colors.grey));      final _darkTheme = ThemeData.dark().copyWith(        backgroundColor: Color(0xFF303030),        tabBarTheme: TabBarTheme(labelColor: Colors.white, unselectedLabelColor: Colors.grey));      final MediaQueryData _mediaQuery = MediaQuery.of(context);      final ThemeData _theme =      _mediaQuery.platformBrightness == Brightness.light        ? _lightTheme        : _darkTheme;      Widget _buildPage(Widget page) {        return KeepAliveWidget(page);      }      return Scaffold(        body: PageView(          //physics: ClampingScrollPhysics(),//支持左右滑动          physics: NeverScrollableScrollPhysics(), //禁止滑动          children:state.pages.map(_buildPage).toList(),          controller: pageController,          onPageChanged:            (i) => dispatch(MainActionCreator.onTabChanged(i)),        ),        bottomNavigationBar: BottomNavigationBar(          backgroundColor: Colors.grey,          items: <BottomNavigationBarItem>[            BottomNavigationBarItem(              icon: Icon(Icons.home),              title: Text(RString.main_bottom_home_title),            ),            BottomNavigationBarItem(              icon: Icon(Icons.movie_creation),              title: Text(RString.main_bottom_discover_title),            ),            BottomNavigationBarItem(              icon: Icon(Icons.calendar_today),              title: Text(RString.main_bottom_member_title),            ),            BottomNavigationBarItem(              icon: Icon(                Icons.account_circle              ),              title: Text(RString.main_bottom_mine_title),            ),          ],          currentIndex: state.selectedIndex,          onTap: (int index) {            pageController.jumpToPage(index);          },          type: BottomNavigationBarType.fixed,        ),      );    },  );}